import { Link } from "react-router-dom";

function Info(props) {
  const { info } = props;

  return (
    <div className='blog-card'>
      <Link to={`/infos/${info.slug}`}>
        {info.type === "podcast" && (
          <img
            src='/images/podcast-icon.png'
            alt=''
            className='card-icon'
          ></img>
        )}
        {info.type === "blog" && (
          <img src='/images/blog-icon.png' alt='' className='card-icon'></img>
        )}
        {info.type === "video" && (
          <img src='/images/video-icon.png' alt='' className='card-icon'></img>
        )}
        <h1 className='blog-card-title'>{info.title}</h1>
        <p className='blog-card-desc'>{info.description}</p>
        <img src={info.image} className='card-img-top' alt={info.name} />
      </Link>
    </div>
  );
}
export default Info;
