import { useEffect, useReducer } from "react";
import axios from "axios";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import Info from "../components/Info";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, infos: action.payload, loading: false };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function LatestInfoScreen() {
  const [{ loading, error, infos }, dispatch] = useReducer(reducer, {
    infos: [],
    loading: true,
    error: "",
  });

  useEffect(() => {
    const fetchInfoData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get("/api/infos");
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    fetchInfoData();
  }, []);

  const size = 3;
  const firstThreeBlogs = blogs
    .toReversed()
    .slice(0, size)
    .map((blog) => {
      return <Blog key={blog.slug} blog={blog}></Blog>;
    });

  return (
    <div>
      <h1>Latest Blogs</h1>
      <div className='products'>
        {loading ? (
          <LoadingBox />
        ) : error ? (
          <MessageBox variant='danger'>{error}</MessageBox>
        ) : (
          <div>
            {infos.map((info) => (
              <div key={info.slug} sm={6} md={4} lg={3} className='mb-3'>
                <h1>HELLO</h1>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
export default LatestInfoScreen;
