import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import Rating from "./Rating";
import axios from "axios";
import { useContext } from "react";
import { Store } from "../Store";

function Info(props) {
  const { info } = props;

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const {
    cart: { cartItems },
  } = state;

  const addToCartHandler = async (item) => {
    const existItem = cartItems.find((x) => x._id === info._id);
    const quantity = existItem ? existItem.quantity + 1 : 1;
    const { data } = await axios.get(`/api/infos/${item._id}`);
    if (data.countInStock < quantity) {
      window.alert("Sorry. Info is out of stock");
      return;
    }
    ctxDispatch({
      type: "CART_ADD_ITEM",
      payload: { ...item, quantity },
    });
  };

  return (
    <Card>
      <Link to={`/info/${info.slug}`}>
        <img src={info.image} className='card-img-top' alt={info.name} />
      </Link>
      <Card.Body>
        <Link to={`/info/${info.slug}`}>
          <Card.Title>{info.name}</Card.Title>
        </Link>
        <Rating rating={info.rating} numReviews={info.numReviews} />
        <Card.Text>${info.price}</Card.Text>
        {info.countInStock === 0 ? (
          <Button variant='light' disabled>
            Out of stock
          </Button>
        ) : (
          <Button onClick={() => addToCartHandler(info)}>Add to cart</Button>
        )}
      </Card.Body>
    </Card>
  );
}
export default Info;
