import { Link } from "react-router-dom";

function Info(props) {
  const { info } = props;

  const iconFunction = () => {
    if (info.type === "podcast") {
      const typeIcon = "/images/podcast-icon.png";
      console.log(info.type + "podcast");
    } else if (info.type === "video") {
      const typeIcon = "/images/video-icon.png";
    } else {
      const typeIcon = "/images/blog-icon.png";
    }
  };

  iconFunction();

  return (
    <div>
      <Link to={`/infos/${info.slug}`}>
        <img src={info.image} className='card-img-top' alt={info.name} />
        <div className='blog-card'>
          <img className='card-icon' src='images/{icon}-icon.png' alt='icon' />
          <h1 className='blog-card-title'>{info.title}</h1>
          <p className='blog-card-desc'>{info.description}</p>
        </div>
      </Link>
    </div>
  );
}
export default Info;
