import { Link } from "react-router-dom";

function Info(props) {
  const { info } = props;

  const iconFunction = () => {
    if (info.type === "podcast") {
      return <img src='/images/podcast-icon.png' alt='' />;
    } else if (info.type === "video") {
      return <img src='/images/video-icon.png' alt='' />;
    } else {
      return <img src='/images/blog-icon.png' alt='' />;
    }
  };

  iconFunction();

  return (
    <div>
      <Link to={`/infos/${info.slug}`}>
        <img src={info.image} className='card-img-top' alt={info.name} />
        <div className='blog-card'>
          {iconFunction}
          <h1 className='blog-card-title'>{info.title}</h1>
          <p className='blog-card-desc'>{info.description}</p>
        </div>
      </Link>
    </div>
  );
}
export default Info;
