import axios from "axios";
import { useEffect, useReducer } from "react";
import { useParams } from "react-router-dom";
import { Helmet } from "react-helmet-async";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { getError } from "../utils";
import HTMLTagRenderer from "../components/HTMLTagRenderer";
import { AdvancedImage, responsive, placeholder } from "@cloudinary/react";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, info: action.payload, loading: false };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function InfoScreen() {
  const params = useParams();
  const { slug } = params;

  const [{ loading, error, info }, dispatch] = useReducer(reducer, {
    info: [],
    loading: true,
    error: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get(`/api/infos/slug/${slug}`);
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: getError(err) });
      }
    };
    fetchData();
  }, [slug]);

  console.log(info.secure_url);
  console.log(info);

  return loading ? (
    <LoadingBox />
  ) : error ? (
    <MessageBox variant='danger'>{error}</MessageBox>
  ) : (
    <div>
      <div className='main-blog-holder margin-holder'>
        <div className='blog-img-holder'>
          <h1>{info.title}</h1>
          <img className='img-main' src={info.image} alt={info.title}></img>
        </div>
        <div>
          <Helmet>
            <title>{info.title}</title>
          </Helmet>
          <div>
            {/* <AdvancedImage
              cldImg={info.public_id}
              plugins={[responsive(), placeholder()]}
            /> */}
            <iframe
              title={info.title}
              src={info.secure_url}
              width='640'
              height='360'
              // style='height: auto; width: 100%; aspect-ratio: 640 / 360;'
              allow='autoplay; fullscreen; encrypted-media; picture-in-picture'
              frameborder='0'
            ></iframe>
          </div>
          <div className='blog-markup-holder'>
            <HTMLTagRenderer
              allowedTags={[
                "b",
                "div",
                "p",
                "blockquote",
                "caption",
                "h1",
                "h2",
                "h3",
                "h4",
                "h5",
                "i",
                "hr",
                "span",
                "table",
                "tbody",
                "tr",
                "td",
                "em",
                "strong",
                "i",
              ]}
              string={info.blog}
            />
          </div>
        </div>
      </div>
    </div>
  );
}
export default InfoScreen;
