import axios from "axios";
import { useEffect, useReducer } from "react";
import { useParams } from "react-router-dom";
import { Helmet } from "react-helmet-async";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { getError } from "../utils";
import HTMLTagRenderer from "../components/HTMLTagRenderer";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, video: action.payload, loading: false };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function VideoScreen() {
  const params = useParams();
  const { slug } = params;

  const [{ loading, error, video }, dispatch] = useReducer(reducer, {
    video: [],
    loading: true,
    error: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get(`/api/videos/slug/${slug}`);
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: getError(err) });
      }
    };
    fetchData();
  }, [slug]);

  return loading ? (
    <LoadingBox />
  ) : error ? (
    <MessageBox variant='danger'>{error}</MessageBox>
  ) : (
    <div>
      <div className='main-blog-holder margin-holder'>
        <div className='blog-img-holder'>
          <h1>{video.title}</h1>
          <img className='img-main' src={video.image} alt={video.title}></img>
        </div>
        <div>
          <Helmet>
            <title>{video.title}</title>
          </Helmet>
          <div className='blog-markup-holder'>
            <video src={video.source} controls />
          </div>
        </div>
      </div>
    </div>
  );
}
export default VideoScreen;
