import { useEffect, useReducer } from "react";
import axios from "axios";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import AboutCard from "../components/AboutCard";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, infos: action.payload, loading: false };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function AboutScreen() {
  return (
    <div className='margin-holder'>
      <div className='about-screen-holder'>
        <h2 className='new-font'>About Ayodele</h2>
        <h4>Looking to talk? Let's connect and discuss the future of AI!</h4>
        <h2>And This Here Is My Media Kit!</h2>
        <a className='pdf_download' href='newsletter_01.pdf' target='_blank'>
          Check it Out!
        </a>
        <h4>Ayodele's Latest Podcasts & Blogs</h4>
        <div className='talks-holder'>{latestTalk}</div>
      </div>
    </div>
  );
}
export default AboutScreen;
