import { useEffect, useReducer } from "react";
import axios from "axios";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import Info from "../components/Info";
import { Link } from "react-router-dom";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, infos: action.payload, loading: false };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function InfoListScreen() {
  const [
    {
      loading,
      error,
      infos,
      pages,
      loadingCreate,
      loadingDelete,
      successDelete,
    },
    dispatch,
  ] = useReducer(reducer, {
    infos: [],
    loading: true,
    error: "",
  });

  const navigate = useNavigate();
  const { search } = useLocation();
  const sp = new URLSearchParams(search);
  const page = sp.get("page") || 1;

  const { state } = useContext(Store);
  const { userInfo } = state;

  useEffect(() => {
    const fetchInfoData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get("/api/infos");
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    fetchInfoData();
  }, []);

  return (
    <div className='blog-section'>
      {loading ? (
        <LoadingBox />
      ) : error ? (
        <MessageBox variant='danger'>{error}</MessageBox>
      ) : (
        <div className='blog-holder'>
          <h1>Latest Blogs</h1>
          <div className='inner-blog-holder'>
            <div className='margin-holder middle-screen'>
              {/* <Row>
                <Col>
                  <h1 className='new-font'>Products</h1>
                </Col>
                <Col className='col text-end'>
                  <div>
                    <button
                      type='button'
                      className='recurring-button'
                      onClick={createHandler}
                    >
                      Create Product
                    </button>
                  </div>
                </Col>
              </Row> */}

              {/* {loadingDelete && <LoadingBox></LoadingBox>} */}
              {loading ? (
                <LoadingBox></LoadingBox>
              ) : error ? (
                <MessageBox variant='danger'>{error}</MessageBox>
              ) : (
                <>
                  <table className='table'>
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>NAME</th>
                        <th>TYPE</th>
                        <th>ACTIONS</th>
                      </tr>
                    </thead>
                    <tbody>
                      {infos.toReversed().map((info) => (
                        <tr key={info._id}>
                          <td>{info._id}</td>
                          <td>{info.title}</td>
                          <td>{info.type}</td>
                          {/* <td>
                    <Button
                      type='button'
                      variant='light'
                      onClick={() => navigate(`/admin/product/${product._id}`)}
                    >
                      Edit
                    </Button>
                    &nbsp;
                    <Button
                      type='button'
                      variant='light'
                      onClick={() => deleteHandler(product)}
                    >
                      Delete
                    </Button>
                  </td> */}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                  <div>
                    {/* {[...Array(infos).keys()].map((x) => (
              <Link
                className={x + 1 === Number(info) ? "btn text-bold" : "btn"}
                key={x + 1}
                to={`/admin/products?page=${x + 1}`}
              >
                {x + 1}
              </Link>
            ))} */}
                  </div>
                </>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
export default InfoListScreen;
