          Cart
          {cart.cartItems.length > 0 && (
            <Badge pill bg='danger'>
              {cart.cartItems.reduce((a, c) => a + c.quantity, 0)}
            </Badge>
          )}
        </Link>
        {loading ? (
          <LoadingBox />
        ) : error ? (
          <MessageBox variant='danger'>{error}</MessageBox>
        ) : (
          <div className='product-card-holder'>
            {/* <div className='featured'>{featured}</div> */}
